mapper.select:

select id, chat_session_code, user_intent, chat_session_memory, current_message_id, create_time, update_time
from ai_chat_session_info
where id = #{id,jdbcType=BIGINT}

select id, chat_session_code, user_intent, current_message_id, create_time, update_time
from ai_chat_session_info
where chat_session_code = #{chatSessionCode, jdbcType=VARCHAR}

mapper.update:
update ai_chat_session_info
set user_intent = #{userIntent,jdbcType=INTEGER},
  update_time = #{updateTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}
